package week1.day2;

public class OddorEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=19;
		if (a%2 == 0) {
			System.out.println("The given number "+a+" is even number");
		}
		else {
			System.out.println("The given number "+a+" is odd number");

		}

	}

}
